				quoteContents.each(function (index) {
					if (this.nodeType !== 3
						&& this.tagName === "A"
						&& quoteContents.length > index + 2
						&& quoteContents[index + 1].nodeType === 3
						&& quoteContents[index + 2].tagName === "I"
						) {

						quotesToWrap.push(index);
					}
				});
